mapper.select:
select COALESCE(sum(netAmount), 0) as totalNetAmount,
COALESCE(sum(validBetAmount), 0) as totalValidBetAmount,
COALESCE(sum(betAmount), 0) as totalBetAmount,
member_id as memberId,
gameType
from

where 1 =1 and isfs = 0

    AND member_id in

        #{item}


        AND betTime >= #{start}



        AND gameType = #{gameType}



        AND betTime  <=  #{end}


    GROUP  by member_id, gameType

select COALESCE(sum(netAmount), 0) as totalNetAmount,
COALESCE(sum(validBetAmount), 0) as totalValidBetAmount,
COALESCE(sum(betAmount), 0) as totalBetAmount
from

where 1 =1 and isfs = 0 and validBetAmount <> 0

    AND member_id = #{memberId}



    AND betTime >= #{start}



    AND gameType = #{gameType}



    AND betTime  <=  #{end}

select COALESCE(sum(netAmount), 0) as totalNetAmount,
COALESCE(sum(validBetAmount), 0) as totalValidBetAmount,
COALESCE(sum(betAmount), 0) as totalBetAmount
from
(select * ,  substr(updated_at,1, 10) as updateAt ,  substr( betTime,1, 10) as betAt from game_record) game_record
where updateAt > betAt and validBetAmount <> 0 and isfs = 0

    AND member_id = #{memberId}



    AND betTime >= #{start}



    AND gameType = #{gameType}



    AND betTime  <=  #{end}
